import React, { useState }from 'react';
import { AppBar, Toolbar, Typography, Tab, Tabs,  ListItemButton, useMediaQuery, useTheme} from '@mui/material/';
import ShoppingCartOutlinedIcon from '@mui/icons-material/ShoppingCartOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import IconButton from '@mui/material/IconButton'
import MovieIcon from '@mui/icons-material/Movie';
import DrawerComponent from './DrowerComponent';
import { Typewriter } from 'react-simple-typewriter';
import "react-simple-typewriter/dist/"


export const Navbar = () => {
    const [value, setValue] = useState(0);
    const handleClickTab = (e, newValue) => {setValue(newValue);};
    //responsive
    const theme = useTheme();
    const isMatch = useMediaQuery(theme.breakpoints.down('md'));
    return (
        <>
        <AppBar color='primary' position='fixed'>
                <Toolbar>
                <MovieIcon sx={{color:'#ff7700', fontSize: 50}}/>
                <Typography variant="overline" display="block" gutterBottom sx={{marginLeft: 1, color: '#FFFFFF', fontSize:20}}>Cinema </Typography>
                    <span style={{ color: '#ff7700', fontSize:25, marginLeft:'50'}}>
                        <Typewriter 
                            cursor
                            cursorStyle="_"
                            typeSpeed={120}
                            words={['NUMEN']} 
                        /></span>
                        { isMatch ? <DrawerComponent /> : (
                        <>
                            <Tabs onChange={handleClickTab} textColor='secondary' indicatorColor='secondary' value={value}>
                                        <Tab label='Inicio' />
                                        <Tab label='Cartelera' />
                                        <Tab label='Salas' />
                                        <Tab label='Sucursales' />
                                        <Tab label='Contacto' />
                                    <ListItemButton>
                                        <IconButton aria-label="search" color='secondary'>
                                            <SearchOutlinedIcon sx={{ fontSize: 25, color: '#FF7700' }}/>
                                        </IconButton>
                                    </ListItemButton>
                                    <ListItemButton>
                                        <IconButton aria-label="buy" color='secondary' href='#'>
                                            <ShoppingCartOutlinedIcon sx={{ fontSize: 25,color: '#FF7700' }}/>
                                        </IconButton>
                                    </ListItemButton>
                            </Tabs>
                        </>
                        )}            
                </Toolbar>
            </AppBar>

            <DrawerComponent />
        </>
    )
}
